import React, { useState, useEffect } from 'react';
import './App.css';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import AuthPage from './components/Auth/AuthPage';
import ChartRenderer, { generateSampleChartData } from './components/Charts/ChartRenderer';
import { filesAPI, chartsAPI } from './services/api';

function AppContent() {
  const { isAuthenticated, user, logout, loading } = useAuth();
  const [activeTab, setActiveTab] = useState('upload');

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading Excel Visualizer...</p>
      </div>
    );
  }

  if (!isAuthenticated) {
    return <AuthPage />;
  }

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-content">
          <div>
            <h1>Excel Visualizer</h1>
            <p>Transform your Excel data into beautiful charts and visualizations</p>
          </div>
          <div className="user-menu">
            <span className="welcome-text">Welcome, {user?.firstName || user?.username}!</span>
            <button className="logout-btn" onClick={logout}>Logout</button>
          </div>
        </div>
      </header>

      <nav className="tab-navigation">
        <button 
          className={`tab ${activeTab === 'upload' ? 'active' : ''}`}
          onClick={() => setActiveTab('upload')}
        >
          Upload Excel
        </button>
        <button 
          className={`tab ${activeTab === 'charts' ? 'active' : ''}`}
          onClick={() => setActiveTab('charts')}
        >
          My Charts
        </button>
        <button 
          className={`tab ${activeTab === 'gallery' ? 'active' : ''}`}
          onClick={() => setActiveTab('gallery')}
        >
          Chart Gallery
        </button>
      </nav>

      <main className="app-content">
        {activeTab === 'upload' && <FileUploadSection />}
        {activeTab === 'charts' && <MyChartsSection />}
        {activeTab === 'gallery' && <ChartGallerySection />}
      </main>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

function FileUploadSection() {
  const [dragActive, setDragActive] = useState(false);
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadResult, setUploadResult] = useState(null);
  const [error, setError] = useState('');

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragActive(true);
  };

  const handleDragLeave = () => {
    setDragActive(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && (droppedFile.name.endsWith('.xlsx') || droppedFile.name.endsWith('.xls'))) {
      setFile(droppedFile);
      setError('');
    } else {
      setError('Please select a valid Excel file (.xlsx or .xls)');
    }
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError('');
    }
  };

  const handleUpload = async () => {
    if (!file) return;

    setUploading(true);
    setUploadProgress(0);
    setError('');

    try {
      const result = await filesAPI.uploadFile(file, (progress) => {
        setUploadProgress(progress);
      });
      
      setUploadResult(result);
      setFile(null);
    } catch (error) {
      setError(error.response?.data?.message || 'Upload failed. Please try again.');
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  };

  return (
    <div className="section">
      <h2>Upload Excel File</h2>
      <div 
        className={`upload-area ${dragActive ? 'drag-active' : ''} ${file ? 'file-selected' : ''}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {!file ? (
          <>
            <div className="upload-icon">📊</div>
            <p>Drag and drop your Excel file here</p>
            <p>or</p>
            <label className="file-input-label">
              Choose File
              <input 
                type="file" 
                accept=".xlsx,.xls" 
                onChange={handleFileInput}
                style={{ display: 'none' }}
              />
            </label>
            <p className="file-info">Supports .xlsx and .xls files</p>
          </>
        ) : (
          <div className="file-preview">
            <div className="file-icon">📄</div>
            <div className="file-details">
              <h3>{file.name}</h3>
              <p>Size: {(file.size / 1024 / 1024).toFixed(2)} MB</p>
              <div className="file-actions">
                <button className="btn btn-primary">Process File</button>
                <button className="btn btn-secondary" onClick={() => setFile(null)}>Remove</button>
              </div>
            </div>
          </div>
        )}
      </div>
