import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('upload');

  return (
    <div className="App">
      <header className="app-header">
        <h1>Excel Visualizer</h1>
        <p>Transform your Excel data into beautiful charts and visualizations</p>
      </header>

      <nav className="tab-navigation">
        <button 
          className={`tab ${activeTab === 'upload' ? 'active' : ''}`}
          onClick={() => setActiveTab('upload')}
        >
          Upload Excel
        </button>
        <button 
          className={`tab ${activeTab === 'charts' ? 'active' : ''}`}
          onClick={() => setActiveTab('charts')}
        >
          My Charts
        </button>
        <button 
          className={`tab ${activeTab === 'gallery' ? 'active' : ''}`}
          onClick={() => setActiveTab('gallery')}
        >
          Chart Gallery
        </button>
      </nav>

      <main className="app-content">
        {activeTab === 'upload' && <FileUploadSection />}
        {activeTab === 'charts' && <MyChartsSection />}
        {activeTab === 'gallery' && <ChartGallerySection />}
      </main>
    </div>
  );
}

function FileUploadSection() {
  const [dragActive, setDragActive] = useState(false);
  const [file, setFile] = useState(null);

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragActive(true);
  };

  const handleDragLeave = () => {
    setDragActive(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && (droppedFile.name.endsWith('.xlsx') || droppedFile.name.endsWith('.xls'))) {
      setFile(droppedFile);
    }
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  return (
    <div className="section">
      <h2>Upload Excel File</h2>
      <div 
        className={`upload-area ${dragActive ? 'drag-active' : ''} ${file ? 'file-selected' : ''}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {!file ? (
          <>
            <div className="upload-icon">📊</div>
            <p>Drag and drop your Excel file here</p>
            <p>or</p>
            <label className="file-input-label">
              Choose File
              <input 
                type="file" 
                accept=".xlsx,.xls" 
                onChange={handleFileInput}
                style={{ display: 'none' }}
              />
            </label>
            <p className="file-info">Supports .xlsx and .xls files</p>
          </>
        ) : (
          <div className="file-preview">
            <div className="file-icon">📄</div>
            <div className="file-details">
              <h3>{file.name}</h3>
              <p>Size: {(file.size / 1024 / 1024).toFixed(2)} MB</p>
              <div className="file-actions">
                <button className="btn btn-primary">Process File</button>
                <button className="btn btn-secondary" onClick={() => setFile(null)}>Remove</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

function MyChartsSection() {
  const sampleCharts = [
    { id: 1, name: 'Sales Report Q4', type: 'Bar Chart', created: '2024-10-10' },
    { id: 2, name: 'Revenue Trends', type: 'Line Chart', created: '2024-10-08' },
    { id: 3, name: 'Market Analysis', type: '3D Pie Chart', created: '2024-10-05' }
  ];

  return (
    <div className="section">
      <h2>My Charts</h2>
      <div className="charts-grid">
        {sampleCharts.map(chart => (
          <div key={chart.id} className="chart-card">
            <div className="chart-thumbnail">📈</div>
            <div className="chart-info">
              <h3>{chart.name}</h3>
              <p>{chart.type}</p>
              <p className="chart-date">Created: {chart.created}</p>
              <div className="chart-actions">
                <button className="btn btn-small">View</button>
                <button className="btn btn-small">Edit</button>
                <button className="btn btn-small btn-danger">Delete</button>
              </div>
            </div>
          </div>
        ))}
        {sampleCharts.length === 0 && (
          <div className="empty-state">
            <p>No charts created yet</p>
            <p>Upload an Excel file to get started</p>
          </div>
        )}
      </div>
    </div>
  );
}

function ChartGallerySection() {
  const chartTypes = [
    { type: 'Bar Chart', icon: '📊', description: 'Perfect for comparing categories' },
    { type: 'Line Chart', icon: '📈', description: 'Great for showing trends over time' },
    { type: 'Pie Chart', icon: '🥧', description: 'Ideal for showing parts of a whole' },
    { type: '3D Bar Chart', icon: '📊', description: '3D visualization for impact' },
    { type: 'Scatter Plot', icon: '⚫', description: 'Shows relationships between variables' },
    { type: 'Area Chart', icon: '🏔️', description: 'Displays cumulative data' }
  ];

  return (
    <div className="section">
      <h2>Chart Gallery</h2>
      <p>Choose from various chart types for your data visualization</p>
      <div className="gallery-grid">
        {chartTypes.map((chart, index) => (
          <div key={index} className="gallery-item">
            <div className="gallery-icon">{chart.icon}</div>
            <h3>{chart.type}</h3>
            <p>{chart.description}</p>
            <button className="btn btn-outline">Try This Chart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
